import React, { Fragment } from 'react'
import { Link } from 'react-router-dom'

import MetaData from '../layout/MetaData'

import { useDispatch, useSelector } from 'react-redux'
import { addItemToCart, removeItemFromCart } from '../../actions/cartActions'

const Cart = ({ history }) => {
    const dispatch = useDispatch();
    const { cartItems } = useSelector(state => state.cart);

    const removeCartItemHandler = (id) => {
        dispatch(removeItemFromCart(id));
    };

    const increaseQty = (id, quantity, stock, size) => {
        const newQty = quantity + 1;
        if (newQty > stock) return;

        // Tìm sản phẩm trong cartItems
        const item = cartItems.find((item) => item.product === id);

        // Tính lại giá dựa trên kích thước
        const price = size === '12 inch' ? (item.price * 1.3) : item.price;
        dispatch(addItemToCart(id, newQty, size, price));
    };

    const decreaseQty = (id, quantity, size) => {
        const newQty = quantity - 1;
        if (newQty <= 0) return;

        // Tìm sản phẩm trong cartItems
        const item = cartItems.find((item) => item.product === id);

        // Tính lại giá dựa trên kích thước
        const price = size === '12 inch' ? (item.price * 1.3) : item.price;
        dispatch(addItemToCart(id, newQty, size, price));
    };


    const checkoutHandler = () => {
        history.push('/login?redirect=shipping');
    };

    return (
        <Fragment>
            <MetaData title={'Giỏ hàng của bạn'} />
            {cartItems.length === 0 ? (
                <div className="container-fluid mt-100">
                    <div className="row">
                        <div className="col-md-12">
                            <div className="card">
                                <div className="card-header">
                                    <h5>Giỏ hàng</h5>
                                </div>
                                <div className="card-body cart">
                                    <div className="col-sm-12 empty-cart-cls text-center">
                                        <img src="https://cdn-icons-png.flaticon.com/512/2037/2037457.png" width={130} height={130} className="img-fluid mb-4 mr-3" alt='' />
                                        <h3><strong>Giỏ hàng của bạn trống</strong></h3>
                                        <h4>Hãy thêm một cái gì đó để giỏ hàng đầy ắp tình yêu thương!</h4>
                                        <Link to="/" className="btn btn-primary cart-btn-transform m-3" data-abc="true">Tiếp tục mua sắm</Link>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            ) : (
                <Fragment>
                    <h3 className="mt-5">Giỏ hàng của bạn đang có: <b>{cartItems.length} sản phẩm</b></h3>
                    <div className="row d-flex justify-content-between">
                        <div className="col-12 col-lg-8">
                            {cartItems.map(item => (
                                <Fragment key={item.product}>
                                    <hr />
                                    <div className="cart-item">
                                        <div className="row">
                                            <div className="col-4 col-lg-3">
                                                <img src={item.image} alt="Laptop" height="90" width="115" />
                                            </div>
                                            <div className="col-5 col-lg-3">
                                                <Link to={`/product/${item.product}`}>{item.name}</Link>
                                            </div>
                                            <div className="col-4 col-lg-2 mt-4 mt-lg-0">
                                            <p className='text-primary'>{(item.price).toLocaleString()}đ</p>
                                            </div>
                                            <div className="col-4 col-lg-3 mt-4 mt-lg-0">
                                                <div className="stockCounter d-inline">
                                                    <span className="btn btn-danger minus" onClick={() => decreaseQty(item.product, item.quantity)}>-</span>
                                                    <input type="number" className="form-control count d-inline" value={item.quantity} readOnly />
                                                    <span className="btn btn-primary plus" onClick={() => increaseQty(item.product, item.quantity, item.stock)}>+</span>
                                                </div>
                                            </div>
                                            <div className="col-4 col-lg-1 mt-4 mt-lg-0">
                                                <i id="delete_cart_item" className="fa fa-trash btn btn-danger" onClick={() => removeCartItemHandler(item.product)}></i>
                                            </div>
                                        </div>
                                    </div>
                                    <hr />
                                </Fragment>
                            ))}
                        </div>

                        <div className="col-12 col-lg-4 my-4">
                            <div id="order_summary">
                                <h4 className='text-center'>Tổng giá trị đơn hàng</h4>
                                <hr />
                                <p>Số lượng: <span className="order-summary-values">{cartItems.reduce((acc, item) => (acc + Number(item.quantity)), 0)} (Sản phẩm)</span></p>
                                <p>Tổng tiền: <span className="order-summary-values">{(cartItems.reduce((acc, item) => acc + item.quantity * item.price, 0)).toLocaleString()}đ</span></p>
                                <hr />
                                <button id="checkout_btn" className="btn btn-primary btn-block" onClick={checkoutHandler}>Đặt hàng</button>
                            </div>
                        </div>
                    </div>
                </Fragment>
            )}
        </Fragment>
    );
};

export default Cart;
