import axios from 'axios'
import { ADD_TO_CART, REMOVE_ITEM_CART, SAVE_SHIPPING_INFO } from '../constants/cartConstants'

export const addItemToCart = (id, quantity, size) => async (dispatch, getState) => {
    const { data } = await axios.get(`/api/v1/product/${id}`)

    // Tính toán giá mới nếu kích thước là 12 inch
    const newPrice = size === '12' ? data.product.price * 1.3 : data.product.price;

    dispatch({
        type: ADD_TO_CART,
        payload: {
            product: data.product._id,
            name: data.product.name,
            price: newPrice,  // Dùng giá mới đã tính toán
            image: data.product.images[0].url,
            stock: data.product.stock,
            quantity,
            size,  // Lưu kích thước vào giỏ hàng
        }
    })

    // Lưu giỏ hàng vào localStorage
    localStorage.setItem('cartItems', JSON.stringify(getState().cart.cartItems))
}

export const removeItemFromCart = (id) => async (dispatch, getState) => {
    dispatch({
        type: REMOVE_ITEM_CART,
        payload: id
    })

    // Lưu lại giỏ hàng sau khi xóa
    localStorage.setItem('cartItems', JSON.stringify(getState().cart.cartItems))
}

export const saveShippingInfo = (data) => async (dispatch) => {
    dispatch({
        type: SAVE_SHIPPING_INFO,
        payload: data
    })

    localStorage.setItem('shippingInfo', JSON.stringify(data))
}
